import React, { Component } from 'react'
import Post from './Post'
import SearchBox from './SearchBox';
class HomePage extends Component {
    constructor() {
        super()

        this.state = { posts: [],searchfield: ''}
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then((response) => response.json())
                .then((response) => this.setState({
                    posts : response
                }))
    }
    onSearchChange = (event) => {
        console.log("calling")
        this.setState({ searchfield: event.target.value })
      }


    render() {

        const { posts, searchfield } = this.state;
        const filteredRobots = posts.filter(post =>{
          return post.title.toLowerCase().includes(searchfield.toLowerCase());
        })

        // let posts = this.state.posts.map((element, key) => 
        //     <Post key={key} id={element.id} title={element.title} description={element.body}/>
        // )
        return (
                <div>
          <SearchBox searchChange={this.onSearchChange}/>
                    <div className="container articles-container">
                        <Post posts={filteredRobots}/>
                        // { posts }
                    </div>
                </div>
        )
    }
}

export default HomePage
