import React, { Component } from 'react'
import Pagination from './Pagination'
import Post from './Post'


class HomePage extends Component {
    constructor() {
        super()

        this.state = { posts: [] , currentPage:1, recordsPerPage: 10}
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then((response) => response.json())
                .then((response) => this.setState({
                    posts : response
                }))
    }

    render() {
        let posts = this.state.posts.map((element, key) => 
            <Post key={key} id={element.id} title={element.title} description={element.body}/>
        )
        const indexOfLastRecord = this.state.currentPage * this.state.recordsPerPage;
        const indexOfFirstRecord = indexOfLastRecord - (this.state.recordsPerPage);
        const currentRecords = posts.slice(indexOfFirstRecord, indexOfLastRecord);
        const nPages = Math.ceil(posts.length / (this.state.recordsPerPage))
        return (
            <div>
                    <div className="container articles-container">
                        { posts }
                    </div>
                    <Pagination
                nPages={this.nPages}
                currentPage={this.state.currentPage}
                setCurrentPage={this.state.setCurrentPage}
            />
            </div>
        )
    }
}

export default HomePage
