import React, { Component } from 'react'
import Post from './Post'
import SearchBox from './SearchBox';
import PostList from './PostList';


class HomePage extends Component {
    constructor() {
        super()

        this.state = { posts: [] }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then((response) => response.json())
                .then((response) => this.setState({
                    posts : response,
                    searchfield: ''
                }))
    }
    onSearchChange = (event) => {
        console.log("calling",event)
        this.setState({ searchfield: event.target.value })
      }

    render() {
        const { posts, searchfield } = this.state;
        const filteredPosts = posts.filter(post =>{
          return post.name.toLowerCase().includes(searchfield.toLowerCase());
        })
        let postsData = this.state.posts.map((element, key) => 
            <Post key={key} id={element.id} title={element.title} description={element.body}/>
        )
        return (
            <div>
                 <SearchBox searchChange={this.onSearchChange}/>
                 <PostList posts={filteredPosts} />
                    <div className="container articles-container">
                        { postsData }
                    </div>
            </div>
        )
    }
}

export default HomePage
